import React from 'react'
import {connect} from 'react-redux'
import InformPage from './InformPage';
import {getInform,updateInform} from '../../../redux/InformReducer'
import { compose } from 'redux';
import { WithAuthRedirect } from '../../../hoc/WithAuthRedirect';
import {SuccessErrorsData} from '../../../hoc/SuccessErrorsData'

class InformPageContainer extends React.Component{
  componentWillUnMount(){
      this.props.getInform(this.props.userId);
  }
  render(){
      return(
        <>
        <InformPage {...this.props} updateInform={this.props.updateInform} success={this.props.success} errors={this.props.errors}
        />
        </>
      )
  }
}

let mapDispatchToProps=(state)=>{
    return{
        inform:state.inform.inform,
        userId:state.auth.userId
    }
}


export default compose(
  WithAuthRedirect,
  SuccessErrorsData,
  connect(mapDispatchToProps,{getInform,updateInform})
)(InformPageContainer)
