import React from 'react'
import DetailsPage from './DetailsPage'
import { connect } from 'react-redux'
import { getReviews } from '../../../redux/ReviewsReducer'
import {removeGood,getGoodsThunkById } from '../../../redux/CatalogReducer'
import { setProduct } from '../../../redux/CardReducer'

class DetailsContainer extends React.Component {
    componentDidMount() {
        this.props.getReviews();
    }
    componentDidUpdate(prevProps){
        if(this.props.match.params.id!=prevProps.match.params.id){
            this.props.getGoodsThunkById(this.props.match.params.id)
        }
    }

    render() {
        return (
            <DetailsPage {...this.props} />
        )
    }
}
let mapStateToProps = (state) => {
    return {
        reviews: state.reviews.reviews,
        goodItem: state.goods.goodItem
    }
}
export default connect(mapStateToProps, { getReviews, setProduct ,removeGood,getGoodsThunkById})(DetailsContainer)
