import React from 'react'

export const File=({ input,name, type, meta: { touched, error, warning } }) => {
    delete input.value
    return (
        <div>
        <input id="file" type={type} name={input.name} {...input} className="km-btn-file"  multiple />
        <label htmlFor="file" className="km-button km-button--primary km-btn-file-label">
            <span>Выбрать файл</span>
        </label>
    </div>
    )
}
