import { testAPI } from "../api/api"
const GET_REVIEWS="GET_REVIEWS"


let initialState={
   reviews:null
}

const  ReviewsReducer=(state=initialState,action)=>{
   switch(action.type){
     case GET_REVIEWS:
         return{...state,reviews:action.payload}
   }
} 

//Action
export const setReviews=(payload)=>({type:GET_REVIEWS,payload})


//Thunk Creator
const getReviews=()=>dispacth=>{
    testAPI.getReviews()
    .then(data=>{
        debugger
       dispacth(setReviews(data))
    })
}

export default ReviewsReducer
