import React from "react";
import './Slider.css';
import SliderItem from './SliderItem/SliderItem';
import sliderSettings from './sliderSettings'
import { connect } from "react-redux";
import {setProduct} from '../../redux/CardReducer'
import {getGoods} from '../../redux/CatalogReducer'

class Slider extends React.Component{
  componentDidMount(){
    this.props.getGoods();
    sliderSettings();
  }
  render(){
    return (
      <div   id={this.props.id} className="carousel slide w-100" data-ride="carousel">
      <div className="carousel-inner w-100" role="listbox">
      <div className="carousel-item active">
                                <div class="col-12 col-sm-6 col-md-4 col-lg-3  style__item">
                                    <img src="images/Mask.png" alt=""/>
                                    <h4>Shake It Off Embellished</h4>
                                    <p>MINI DRESS</p>
                                    <h5>€61.17</h5>
                                    <div class="style__btn">
                                        <button>ADD TO CART</button>
                                    </div>
                                </div>
                            </div>
                            <div class="carousel-item">
                                <div class="col-12 col-sm-6 col-md-4  col-lg-3 style__item">
                                    <img src="images/Mask-2.png" alt=""/>
                                    <h4>Shake It Off Embellished</h4>
                                    <p>MINI DRESS</p>
                                    <h5>€61.17</h5>
                                    <div class="style__btn">
                                        <button>ADD TO CART</button>
                                    </div>
                                </div>
                            </div>
      </div>
      <a className="carousel-control-prev"  href={this.props.a} id="arrow-prev" role="button"
          data-slide="prev">
          <div className="arrow">
              <i className="carousel-control-prev-icon fas fa-chevron-left" aria-hidden="true"></i>
              <span className="sr-only">Предыдущий</span>
          </div>
      </a>
      <a className="carousel-control-next"  href={this.props.a} id="arrow-next" role="button"
          data-slide="next">
          <div className="arrow">
              <i className="carousel-control-next-icon fas fa-chevron-right" aria-hidden="true"></i>

              <span className="sr-only">Cледующий</span>
          </div>
      </a>
  </div>
    )
    }
};

let mapStateToProps=(state)=>{
  debugger
  return{
    goods:state.goods.goods
  }
}

export default connect(mapStateToProps,{setProduct,getGoods})(Slider)

