import cn from 'classnames'
import styles from './Paginator.module.css'
import {Link} from 'react-router-dom'

const Paginator = ({totalCount, pageSize, portionSize=3, pageNumber, onPageChanged}) => {
debugger
let pagesCount = Math.ceil(totalCount / pageSize);
let pages = [];
for (var i = 1; i <= pagesCount; i++) {
    pages.push(i);
}
let portionCount=Math.ceil(pagesCount/portionSize)
let [portionNumber,setPortionNumber]=useState(1)
let leftPortion=(portionNumber-1)*portionSize+1;
let rightPortion=portionNumber*portionSize;
return(
    <div className="row d-flex justify-content-center">
   
  {portionNumber>1&&<button onClick={()=>{setPortionNumber(portionNumber-1)}}>prev</button>}
        {pages.filter(i=>i>=leftPortion&&i<=rightPortion).map(i => {
            return <span key={i} >
                <button onClick={(e) => {onPageChanged(i); }} className={pageNumber === i ? styles.selectedPage : null}>{i}</button>
            </span>
        }
    
        )}
        {portionCount>portionNumber&&<button onClick={()=>{setPortionNumber(portionNumber+1)}}>Next</button>} 
    
    
   </div>
