import React from 'react'
import {connect} from 'react-redux'
import InformPage from './InformPage';
import {getInform,updateInform} from '../../../redux/InformReducer'
import { compose } from 'redux';
import { WithAuthRedirect } from '../../../hoc/WithAuthRedirect';


class InformPageContainer extends React.Component{
  componentDidMount(){
      this.props.getInform(this.props.userId);
  }
  render(){
      return(
        <>
        <InformPage {...this.props}
        />
        </>
      )
  }
}

let mapDispatchToProps=(state)=>{
    return{
        inform:state.inform.inform,
        userId:state.auth.userId
    }
}


export default compose(
  WithAuthRedirect,
  connect(mapDispatchToProps,{getInform,updateInform})
)(InformPageContainer)
