import {clearCardItems} from './CardReducer'
import { testAPI } from "../api/api";


const ADD_ORDERS="ADD_ORDERS"


let initialState = {
  orders: [],
  inform:null
};

let OrdersReducer = (state = initialState, action) => {
  switch (action.type) {
      case ADD_ORDERS:
          debugger
          return{...state,orders:[...state.orders,...action.items.items],inform:action.items.inform}
    default:
      return state;
  }
};


 export const addOrders=(items)=>({type:ADD_ORDERS,items})


export const addOrdersThunk=(items)=>dispatch=>{
    debugger
    testAPI.setOrders(items,items.id).then(response=>{
        dispatch(addOrders(items))
        dispatch(clearCardItems(null,null,null))
      })

}

export default OrdersReducer
