import { testAPI } from "../api/api";
import {returnErrors,returnSuccess} from './SuccessErrorReducer'
const GET_INFORM_OF_USER = "GET_INFORM_OF_USER";

let initialState = {
  inform: null,
  isAddInform:false
};

let InformReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_INFORM_OF_USER:
      return { ...state, inform: action.inform,isAddInform:true};
    default:
      return state;
  }
};

const addInform=(inform)=>({type:GET_INFORM_OF_USER,inform})

export const getInform=()=>(dispatch,getState)=>{
  debugger
   testAPI.getInform(getState().auth.userId).then(response=>{
       dispatch(addInform(response.data))
   })
}
export const updateInform=(inform)=>(dispatch,getState)=>{
   testAPI.updateInform((getState().auth.userId),inform).then(response=>{
       dispatch(returnSuccess(response.data.message,response.status,'SUCCESS_USER_INFORM_CHANGE'))
   }).catch(err=>{
    dispatch(returnErrors(err.response.data,err.response.status,'USER_INFORM_CHANGE_ERROR'))
  })
}
export const setInform=(inform)=>(dispatch,getState)=>{
  debugger
  testAPI.setInform((getState().auth.userId),inform).then(response=>{
    dispatch(addInform(response.data))
  })
}

export default InformReducer
