import React from 'react'
import './Input.css'

export const Textarea=({textarea,meta:{touched,error},...props})=>{
    const hasError=error&&touched
    return(
        <div className="formControl">
            <textarea className={hasError?"error":""} {...textarea}{...props} />
              {hasError?<span>{error}</span>:null} 
        </div>
    )
}
