const ADD_ORDERS="ADD_ORDERS"

let initialState = {
  orders: [],
};

let OrdersReducer = (state = initialState, action) => {
  switch (action.type) {
      case ADD_ORDERS:
          return{...state,orders:[...orders,action.payload]}
    default:
      return state;
  }
};


export const addOrders=(payload)=>({type:ADD_ORDERS,payload})

export default OrdersReducer
