import React from 'react'
import './ChangePass.css'
import Button1 from '../../SiteButtons/Button1/Button1'
import {changeUserPass} from '../../../redux/AuthReducer'
import { SuccessErrorsData } from '../../../hoc/SuccessErrorsData'
import {Field,reduxForm} from 'redux-form'
import {Input} from '../../common/FormsControls/Input'
import {connect} from 'react-redux'
import { compose } from 'redux'


const ChangePass = (props) => {
    const onSubmit=(formData)=>{
        props.changeUserPass(formData.oldPass,formData.newPass,formData.verifyPass)
    }
    generateForm = () => {
        return (
            <ReduxChangePassword  onSubmit={onSubmit}  errors={this.props.errors}/> 
        )
    }
    generateSuccessMessage = () => {
        return (
            <div className="row justify-content-center " style={{ color: '#E77E83' }}><h4 className=" d-flex flex-column align-items-center">{this.props.success.message}<br /><i class="fas fa-check-circle"></i></h4>

            </div>

        )
    }
    return (
        <div>
        {this.props.success.message ? this.generateSuccessMessage() : this.generateForm()}
    </div>
    )
}


const ChangePassForm=(props)=>{
return(
    <form className="changePassForm" onSubmit={props.handleSubmit}>
            <h5 className="changePassHeadline">Change Password</h5>
            <div className="form-group">
                <Field type="password"  component={Input} className="form-control" name="oldPass" placeholder="Your old password"
                    required autofocus />
            </div>
            <div className="form-group">
                <Field type="password"  component={Input} className="form-control" name="newPass" placeholder="Your new password"
                    required />
            </div>
            <div className="form-group">
                <Field type="password" component={Input} className="form-control" name="verifyPass" placeholder="Repeat new password"
                    required />
            </div>
            <div className="changePassFormBtn">
                <Button1 text="Save" />
                <button>Save</button>
            </div>
            <div className="row d-flex justify-content-center">
                {props.errors.id === 'CHANGE_PASS_ERROR' ? <Alert color="danger">{props.errors.message.message}</Alert> : null}
            </div>
        </form>
)
}

const ReduxChangePassword = reduxForm({
    form: 'changePassForm'
})(ChangePassForm)


export default compose(
    SuccessErrorsData,
    connect(null, { changeUserPass })
)(ChangePass)
