import React from 'react'
import { connect } from 'react-redux'
import CatalogPage from './CatalogPage'
import { compose } from 'redux'
import { getGoodsThunk, getGoodsThunkById,changePageSize,changePageNumber} from '../../../redux/CatalogReducer'
import { setProduct } from '../../../redux/CardReducer'
import { IsPopUpHook } from '../../../hoc/IsPopUpHook'
import { isPopUp } from '../../../redux/AppReducer'



class CatalogPageContainer extends React.Component {
  componentDidMount() {
    const { pageNumber, pageSize } = this.props;
    this.props.getGoodsThunk(pageNumber, pageSize);
  }
  componentDidUpdate(prevProps){
    if(this.props.pageSize!==prevProps.pageSize){
        const { pageSize } = this.props;
        this.props.changePageSize(pageSize)
      }
  }
  onPageChanged = (pageNumber) => {
    const { pageSize } = this.props;
    this.props.getGoodsThunk(pageNumber,pageSize)
    this.props.changePageNumber(pageNumber)
  }
  render() {
    return (
      <>
        <CatalogPage  {...this.props} onPageChanged={this.onPageChanged} changePageSize={this.props.changePageSize}
        />
      </>
    )
  }
}
let mapStateToProps = (state) => {
  return {
    goods: state.goods.goods,
    token: state.auth.token,
    items: state.card.items,
    pageSize: state.goods.pageSize,
    pageNumber: state.goods.pageNumber,
    totalCount: state.goods.totalCount
  }
}

export default compose(
  IsPopUpHook,
  connect(mapStateToProps, { getGoodsThunk, getGoodsThunkById,changePageNumber, isPopUp, setProduct,changePageSize })
)(CatalogPageContainer)
