import React from 'react'
import './File.css'
export const File=({ input,name, type, meta: { touched, error, warning } }) => {
    delete input.value
    return (
        <div>
            <label className="fileBlock" htmlFor={input.name}>
                <input class="file"{...input} type={type} name={name} multiple/>
            </label>
        </div>
    )
}
