import React from 'react'
import {connect} from 'react-redux'
import { ReduxCheckoutForm } from './CheckoutForm'
import {addOrdersThunk} from '../../../redux/OrdersReducer'

class Checkout extends React.Component{
    onSubmit = (formData) => {
        let data={items:this.props.items,inform:formData,id:this.props.auth.user._id}
        if(!this.props.auth.isAuth){
            this.props.addOrders(data)
            alert("hello")
        }
        else{
            this.props.addOrdersThunk(data)
        }

     }

 render(){
     return(
         <ReduxCheckoutForm onSubmit={this.onSubmit} errors={this.props.errors}/>
     )
 }

}


let mapStateToProps=(state)=>{
    return{
        errors:state.errors,
        items:state.card.items,
        isAuth:state.auth.auth
    }
}
export default connect(mapStateToProps,{addOrdersThunk})(Checkout)
