export const GET_SUCCESS="GET_SUCCESS"
export const CLEAR_SUCCESS="CLEAR_SUCCESS"

const initialState={
    message:null,
    status:null,
    id:null
}

const SuccessReducer=(state=initialState,action)=>{
    switch(action.type){
      case GET_SUCCESS:{
          return{
              message:action.payload.message,
              status:action.payload.status,
              id:action.payload.id
          }
      }
      case CLEAR_SUCCESS:{
        return{message:{},status:null,id:null}
    }
    default:return state;
    }
}


export const returnSuccess=(message,status,id=null)=>({type:GET_SUCCESS,payload:{message,status,id}})
export const clearSuccess=()=>({type:CLEAR_SUCCESS})


export default SuccessReducer
