class Checkout extends React.Component{
    onSubmit = (formData) => {
        debugger

       this.props.addOrdersThunk({items:this.props.items,inform:formData});
    }
 render(){
     return(
         <ReduxCheckoutForm onSubmit={this.onSubmit} errors={this.props.errors}/>
     )
 }
}


let mapStateToProps=(state)=>{
    return{
        errors:state.errors,
        items:state.card.items
    }
}
export default connect(mapStateToProps,{addOrdersThunk})(Checkout)
