import {testAPI} from '../api/api'
const SET_REVIEWS="SET_REVIEW"

let initialState={
    reviews:null
}

const ReviewsReducer=(state=initialState,action)=>{
    switch (action.type) {
        case SET_REVIEWS:
            debugger
   return{...state,reviews:action.item}
        default:
            return state;      
    }
}

export const setItemOfReview=(item)=>({type:SET_REVIEWS,item})

export const setReviews=(name,photo,images,reviews,rating,goodsId)=>dispatch=>{
    testAPI.setReviews(name,photo,images,reviews,rating,goodsId)
  }

export default ReviewsReducer
