import {
  USER_LOADING,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT_SUCCESS,
  REGISTER_SUCCESS,
} from "./actions";

const initialState = {
  token: localStorage.getItem("token"),
  isAuth: null,
  isLoading: false,
  user: null,
};

const authTestReducer = (state = initialState, action) => {
  switch (action.type) {
    case USER_LOADING:
      return { ...state, isLoading: true };
    case USER_LOADED:
      return { ...state, isLoading: false, isAuth: true, user: action.payload };
    case LOGIN_SUCCESS:
    case REGISTER_SUCCESS:
      return { ...state, ...action.payload, isAuth: true, isLoading: false };
    case AUTH_ERROR:
    case LOGIN_FAIL:
    case REGISTER_FAIL:
    case LOGOUT_SUCCESS:
      return {
        ...state,
        isAuth: false,
        isLoading: false,
        user: null,
        token: null,
      };
    default:
      return state;
  }
};






export default authTestReducer;
