import {createSelector} from 'reselect'
import { sortBy } from '../utils/helperFunctions/sortBy'

export const getGoods=(state)=>{
    return state.goods.goods
}
export const getTotalGoods=createSelector(getGoods,(items)=>{
   return  sortBy(items, state.goods.filterBy)
})
