import React, { useEffect } from 'react'
import './ChangePass.css'
import Button1 from '../../SiteButtons/Button1/Button1'
import {changeUserPass} from '../../../redux/AuthReducer'
import { SuccessErrorsData } from '../../../hoc/SuccessErrorsData'
import {Field,reduxForm} from 'redux-form'
import {Input} from '../../common/FormsControls/Input'
import {connect} from 'react-redux'
import { compose } from 'redux'
import { Alert } from 'reactstrap'
import {clearSuccess} from '../../../redux/SuccessReducer'
import ChangePassPage from './ChangePassForm'

class ChangePass extends React.Component {
     componentDidMount(){
         this.props.clearSuccess();
     }
     onSubmit=(formData)=>{
        this.props.changeUserPass(formData.oldPass,formData.newPass,formData.verifyPass)
    }
    render(){
        return(
               <ChangePassPage onSubmit={onSubmit} success={this.props.success} errors={this.props.errors}/>
        )
    }
}





export default compose(
    SuccessErrorsData,
    connect(null, { changeUserPass,clearSuccess})
)(ChangePass)
