import React from 'react'
import DetailsPage from './DetailsPage'
import { connect } from 'react-redux'
import {getReviews} from '../../../redux/ReviewsReducer'
import {getGoodsThunkById} from '../../../redux/CatalogReducer'
import {setProduct} from '../../../redux/CardReducer'

class DetailsContainer extends React.Component{
    componentDidMount(){
        const id=this.props.match.params.id;
        if(id){
        this.props.getGoodsThunkById(id)
        this.props.getReviews();}
    }
    componentDidUpdate(prevProps){
        const id=this.props.match.params.id;
        if(id!=prevProps.id){
        this.props.getGoodsThunkById(id)
        this.props.getReviews();}
    }
    render(){
        return(
            <DetailsPage {...this.props}/>
        )
    }
}
let mapStateToProps=(state)=>{
    return{
        reviews:state.reviews.reviews,
        goodItem:state.goods.goodItem
    }
}
export default connect(mapStateToProps,{getReviews,getGoodsThunkById,setProduct})(DetailsContainer)
