import {createSelector} from 'reselect'
import { sortBy } from '../utils/helperFunctions/sortBy'

export const getGoods=(state)=>{
    return state.goods.goods,state.goods.filterBy
}
export const getTotalGoods=createSelector(getGoods,(items,filterBy)=>{
   return  sortBy(items,filterBy)
})
