import React from 'react'
import { connect } from 'react-redux'
import CatalogPage from './CatalogPage'
import { compose } from 'redux'
import { getGoodsThunk,changeFilterBy, getGoodsThunkById,changePageNumber} from '../../../redux/CatalogReducer'
import { setProduct } from '../../../redux/CardReducer'
import { IsPopUpHook } from '../../../hoc/IsPopUpHook'
import { isPopUp } from '../../../redux/AppReducer'
import {orderBy} from 'lodash'


class CatalogPageContainer extends React.Component {
  componentDidMount() {
    const { pageNumber, pageSize } = this.props;
    this.props.getGoodsThunk(pageNumber, pageSize);
  }
  sortBy=(goods,filterBy)=>{
    switch (filterBy) {
      case 'Рекомендации':
        return goods;
      case 'От дорогим к дешевым':
        return orderBy(goods,'price','desc')
        break;

      default:
        break;
    }
  }
  onPageChanged = (pageNumber) => {
    const { pageSize } = this.props;
    debugger
    this.props.getGoodsThunk(pageNumber,pageSize)
    this.props.changePageNumber(pageNumber)
  }
  changePageSize=(pageSize,pageNumber=1)=>{
    debugger
    this.props.getGoodsThunk(pageNumber,pageSize)
  }
  render() {
    return (
      <>
        <CatalogPage  {...this.props} onPageChanged={this.onPageChanged}changeFilterBy={this.props.changeFilterBy} changePageSize={this.changePageSize}
        />
      </>
    )
  }
}
let mapStateToProps = (state) => {
  return {
    goods: state.goods.goods,
    token: state.auth.token,
    items: state.card.items,
    pageSize: state.goods.pageSize,
    pageNumber: state.goods.pageNumber,
    totalCount: state.goods.totalCount
  }
}

export default compose(
  IsPopUpHook,
  connect(mapStateToProps, { getGoodsThunk, getGoodsThunkById,changePageNumber, isPopUp, setProduct,changeFilterBy})
)(CatalogPageContainer)
